mapper.insert:
INSERT INTO tb_user (id, name, age, email, account, password, dept_id)
VALUES
    (#{user.id}, #{user.name}, #{user.age}, #{user.email}, #{user.account}, #{user.password}, #{user.deptId});

mapper.update:
UPDATE tb_user
SET name = #{user.name}, age = #{user.age}
    WHERE id = #{user.id};

mapper.select:
select tb_user.id, tb_user.name, tb_dept.id as deptId, tb_dept.name as deptName from tb_user Left join tb_dept on tb_user.dept_id = tb_dept.id where 1 = 1
and tb_user.name like '%' #{userInfo.name} '%'
and tb_dept.id = #{userInfo.deptId}

select count(1) from tb_user
where 1 = 1
  and tb_user.password = #{user.password} and tb_user.account = #{user.username}
